export default class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      
      showUserNameError: false,
      showPasswordError: false,
      modeDateTimePicker: 'time',
      userNameString: '',
      passwordString: '',
      loading: false,
         

    };
   
  }
  componentDidMount = async () => 
  {
  
    
  }
  

  render() {

   const { open, value, items } = this.state;

    return (
      
      <View style ={styles.container}>
       
        <StatusBar backgroundColor='#4387bb' barStyle={'light-content'} />
<SafeAreaView>
<View style={styles.logoIconContainer}>
<SvgXml xml={logoXML} />
</View>

<View style={styles.BText}>
<SvgXml xml={BXML} />
<SvgXml xml={PXML} left={4}/>
<SvgXml xml={MXML} left={7}/>
</View>

<View style={styles.walletTextContainer}>
<SvgXml xml={WXML} />
<SvgXml xml={AXML} left={2} />
<SvgXml xml={L1XML} left={8} top={-6}/>
<SvgXml xml={L2XML} left={14} top={-6}/>
<SvgXml xml={EXML} left={17}/>
<SvgXml xml={TXML} left={19} top={-4}/>
</View>
          </SafeAreaView>


          <TouchableOpacity
                onPress={() => this.props.navigation.navigate('SignInPage')}
                style={styles.SignInButton}>
                  <SvgXml xml={SignInButtonXML} />
                </TouchableOpacity>


                <View style = {styles.donthaveanAccount}>
<Text style={styles.donthaveanAccountText}>{"Don't have an account? "}</Text> 
<TouchableOpacity
      style={styles.SignUp}
      onPress={() => this.props.navigation.navigate('SignUpPersonalDetailsPage')}>
    <Text style={styles.signupText}>
      Sign Up
    </Text>
    </TouchableOpacity>
</View>

      </View>
      
    );
  }



}

